mapper.select:
select g.gongGaoMingCheng,g.faBuShiJian,g.lanMu,g.neiRong,g.liuLanShu,r.xingMing,g.id as id1 from gonggao as g join renyuandangan as r on (g.buMenId = #{buMenId} or g.buMenId = 0 or g.dangAnId = #{dangAnId}) and g.dangAnId = r.id and g.zhuangTai = 1
and (g.gongGaoMingCheng like concat(concat('%',#{souSuo}),'%') or g.neiRong like concat(concat('%',#{souSuo}),'%') or r.xingMing like concat(concat('%',#{souSuo}),'%'))
order by faBuShiJian desc;

select count(id)
from gonggao
where dangAnId = #{dangAnId}
  and zhuangTai = #{zhuangTai};

mapper.insert:
insert into gonggao(dangAnId, gongGaoMingCheng, faBuShiJian, lanMu, neiRong, fuJianDiZhi, buMenId, zhuangTai)
VALUES (#{dangAnId}, #{gongGaoMingCheng}, now(), #{lanMu}, #{neiRong}, #{fuJianDiZhi}, #{buMenId},
        #{zhuangTai});

mapper.update:
update gonggao
set gongGaoMingCheng = #{gongGaoMingCheng},
    faBuShiJian      = now(),
    lanMu            = #{lanMu},
    neiRong          = #{neiRong},
    fuJianDiZhi      = #{fuJianDiZhi},
    buMenId          = #{buMenId},
    zhuangTai        = #{zhuangTai}
where dangAnId = #{dangAnId}
  and id = #{id}

update gonggao
set liuLanShu=#{liuLanShu}
where id = #{id};

mapper.select:
select bm.buMenMingCheng
from gonggao as g
         join oadb.bumen as bm on bm.id = g.buMenId and g.id = #{id}

mapper.update:
update gonggao
set zhuangTai = #{zhuangTai}

    , faBuShiJian = now()

where id = #{id};

mapper.select:
select count(id)
from gonggao
where dangAnId = #{dangAnId}
  and faBuShiJian > (select gongGaoDianJiShiJian from renyuandangan where id = #{dangAnId});
