mapper.insert:

insert into gongzuojilu (id, leiXing, shiJian, 
  dangAnId, zhuangTai, jieShouRenId
  )
values (#{id,jdbcType=INTEGER}, #{leixing,jdbcType=VARCHAR}, #{shijian,jdbcType=TIMESTAMP}, 
  #{danganid,jdbcType=INTEGER}, #{zhuangtai,jdbcType=INTEGER}, #{jieshourenid,jdbcType=INTEGER}
  )

mapper.update:
update gongzuojilu set id=#{id}

  ,neiRong=#{neiRong}


  ,shiJian=now()


  ,leiXing=#{leiXing}


  ,jieShouRenId=(select id from  renyuandangan where xingMing=#{pingLunRen})

where id=#{id} and dangAnId=#{dangAnId}

mapper.select:
select g.id,g.neiRong,g.shiJian,g.dangAnId,g.leiXing,(select renyuandangan.xingMing
from oadb.renyuandangan where renyuandangan.id=g.dangAnId) as xingMing,
g.zhuangTai,g.jieShouRenId,(select id from rizhipinglun where riZhiId=g.id) as riZhiPingLunId,(select r.pingLunNeiRong from oadb.rizhipinglun as  r where g.id =r.riZhiId)
as pingLunNeiRong,(select  r.riQi from oadb.rizhipinglun as r where g.id =r.riZhiId) as riQi,
(select renyuandangan.xingMing from oadb.renyuandangan where renyuandangan.id= g.jieShouRenId) as pingLunRen
from oadb.gongzuojilu g where  g.dangAnId=#{dangAnId} and g.zhuangTai!=-1

  and g.shiJian  >=#{startDate} and g.shiJian <=#{endDate}


  and g.leiXing =#{type}

select g.id,g.neiRong,g.shiJian,g.dangAnId,g.leiXing,(select renyuandangan.xingMing
from oadb.renyuandangan where renyuandangan.id=g.dangAnId) as xingMing,
g.zhuangTai,g.jieShouRenId,(select id from rizhipinglun where riZhiId=g.id) as riZhiPingLunId,(select r.pingLunNeiRong from oadb.rizhipinglun as  r where g.id =r.riZhiId)
as pingLunNeiRong,(select  r.riQi from oadb.rizhipinglun as r where g.id =r.riZhiId) as riQi,
(select renyuandangan.xingMing from oadb.renyuandangan where renyuandangan.id= g.jieShouRenId) as pingLunRen
from oadb.gongzuojilu g where  g.jieShouRenId=#{dangAnId} and g.zhuangTai!=-1

  and g.shiJian  >=#{startDate} and g.shiJian <=#{endDate}


  and g.leiXing =#{type}
