mapper.select:
select kaoqin.id id1,renyuandangan.id id2,bumen.id id3, xingMing,buMenMingCheng,shangWuShangBan, xiaWuXiaBan, zhuangTai, qingJiaZhuangTai, dangTianRiQi from kaoqin,renyuandangan,bumen where kaoqin.dangAnId =renyuandangan.id and buMenId =bumen.id
and xingMing like concat(concat('%',#{xingMing}),'%')
and buMenId = #{buMenId}
and dangTianRiQi >#{startTime}
and #{endTime} > dangTianRiQi

select shangWuShangBan, xiaWuXiaBan, kaoqin.zhuangTai, qingJiaZhuangTai, dangTianRiQi
from kaoqin
where dangAnId = #{dangAnId}

    and dangTianRiQi between #{startDate} and #{endDate}


    and dangTianRiQi like concat(#{date},'%')


    and zhuangTai = #{zhuangTai}

select dangTianRiQi,qingJiaShiYou,qingjiabiao.zhuangTai from kaoqin,qingjiabiao where dangAnId = #{dangAnId} and qingJiaBiaoId = qingjiabiao.id
and dangTianRiQi between #{startDate} and #{endDate}
and dangTianRiQi like concat(#{date},'%')
